INSERT INTO creature_text VALUES(36856, 1, 0, "That's how it's done!", 12, 0, 100, 4, 0, 0, 0, 'Shandy say 2');
INSERT INTO creature_text VALUES(36856, 1, 1, "Well done!", 12, 0, 100, 4, 0, 0, 0, 'Shandy say 2');
INSERT INTO creature_text VALUES(36856, 1, 2, "Aquanos can hardly keep up!", 12, 0, 100, 4, 0, 0, 0, 'Shandy say 2');
INSERT INTO creature_text VALUES(36856, 1, 3, "Nice. Idon't want to know what that stain was.", 12, 0, 100, 4, 0, 0, 0, 'Shandy say 2');
INSERT INTO creature_text VALUES(36856, 2, 0, "See the piles of loundry and the bucket of water? I'll call out what I need next, and you put it in the tub. Ready?", 12, 0, 100, 1, 0, 0, 0, 'Shandy say 3');
INSERT INTO creature_text VALUES(36856, 3, 0, "The tub needs more water!", 12, 0, 100, 25, 0, 0, 0, 'Shandy say water');
INSERT INTO creature_text VALUES(36856, 4, 0, "Quick, add some shirts to the laundry!", 12, 0, 100, 25, 0, 0, 0, 'Shandy say shirts');
INSERT INTO creature_text VALUES(36856, 5, 0, "Toss some pants in to the tub!", 12, 0, 100, 25, 0, 0, 0, 'Shandy say pants');
INSERT INTO creature_text VALUES(36856, 6, 0, "Toss some unmentionables to the tub!", 12, 0, 100, 25, 0, 0, 0, 'Shandy say unmentionables');
INSERT INTO creature_text VALUES(36856, 7, 0, "Aquanos, stop sending the clothes so high! You didn't have to see the look on Aethos Sunreaver's face when he found his pants in the fauntain!", 12, 0, 100, 1, 0, 0, 0, 'Shandy say 4');
INSERT INTO creature_text VALUES(36856, 8, 0, "See how easy that was with everyone working together? Just take what you need from the clean lundry here. But don't forget to return it when you're finished.", 12, 0, 100, 1, 0, 0, 0, 'Shandy say 5');
