INSERT INTO creature_text VALUES (37205, 0, 0, 'We must defend the sunwell from the enemy at all costs.', 12, 0, 100, 1, 0, 16769, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 1, 0, 'I am ordering you to withdraw to the sunwell proper to help prepare the defences.', 12, 0, 100, 25, 0, 16770, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 2, 0, 'I will make my stand here and buy you as much as time as I am able. Use it well, and make our prince proud.', 12, 0, 100, 1, 0, 16771, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 3, 0, 'It has been a great honour to fight beside you in defence of our land, and our people.', 12, 0, 100, 2, 0, 16772, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 4, 0, 'You, stranger! You are not one of my soldiers! Will you stay and stand with me to face the enemy?', 12, 0, 100, 0, 0, 16773, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 5, 0, 'Listen well, scourge defilers! None of you will reach the sunwell, so long as I stand!', 14, 0, 100, 1, 0, 16774, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 6, 0, 'I... I wasn''t meant to survive this attack!', 12, 0, 100, 1, 0, 16775, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 7, 0, 'Why has this happened? Why have you come here?', 12, 0, 100, 1, 0, 16776, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 8, 0, 'It''s Quel''Delar! You possess the sword!', 12, 0, 100, 1, 0, 16777, 0, 'Thalorien Dawnseeker');
INSERT INTO creature_text VALUES (37205, 9, 0, 'I don''t know how, but the blade has chosen you to be its new wielder. Take it, with my blessing, and wield it against the scourge as I once did!', 12, 0, 100, 1, 0, 16778, 0, 'Thalorien Dawnseeker');
