INSERT INTO creature_text VALUES (33480, 0, 0, "Lass, I've a confession to make to ya.", 12, 0, 100, 1, 0, 0, 0, "Ironforge Champion");
INSERT INTO creature_text VALUES (33480, 1, 0, "I've been feelin'... urges.", 12, 0, 100, 1, 0, 0, 0, "Ironforge Champion");
INSERT INTO creature_text VALUES (33480, 2, 0, "It was a powerful feelin' while I was listenin' to yer sermon, and it's drivin' me nuts!", 12, 0, 100, 1, 0, 0, 0, "Ironforge Champion");
INSERT INTO creature_text VALUES (33480, 3, 0, "...I NEED A DRINK!", 12, 0, 100, 1, 0, 0, 0, "Ironforge Champion");
INSERT INTO creature_text VALUES (34102, 10, 0, "Drink in moderation, then, my dwarven friend, but you may want to turn in your lance and reins for the time being. Go with the Light.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
