INSERT INTO creature_text VALUES (33459, 0, 0, "This isn't honorable combat. I long to face my opponents in battle with my axe in hand!", 12, 0, 100, 1, 0, 0, 0, "Orgrimmar Champion");
INSERT INTO creature_text VALUES (33459, 1, 0, "These are my enemies. YOU would be my enemy in battle! Your king has declared war on my kind!", 12, 0, 100, 1, 0, 0, 0, "Orgrimmar Champion");
INSERT INTO creature_text VALUES (33459, 2, 0, "But instead of fighting as my people would, as my people should, I find myself with a long, unwieldy pole, attempting to coax my wolf into charging my opponent without attacking.", 12, 0, 100, 1, 0, 0, 0, "Orgrimmar Champion");
INSERT INTO creature_text VALUES (33459, 3, 0, "This is not how orcs fight! My duty to the Warchief wars with my personal honor. I hold no faith in your Light, but as you seem an advisor, I would hear your words.", 12, 0, 100, 1, 0, 0, 0, "Orgrimmar Champion");
INSERT INTO creature_text VALUES (34102, 20, 0, "I understand your frustration. Your people are ferocious in battle, and this isn't what you're used to.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 21, 0, "But this is a different sort of honorable combat. Difficult to adapt to, perhaps, but with your prowess I'm sure you'll show everyone the strength in your people.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 22, 0, "Argent Confessor Paletress flexes at Orgrimmar Champion.", 16, 0, 100, 23, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (33459, 4, 0, "Orgrimmar Champion laughs.", 16, 0, 100, 11, 0, 0, 0, "Orgrimmar Champion");
INSERT INTO creature_text VALUES (34102, 23, 0, "Besides... while an axe can be a valuable tool in war, lances have their uses as well.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
