INSERT INTO creature_text VALUES (33467, 0, 0, "This fighting is endless. I'm not certain how much more of it I can stand.", 12, 0, 100, 1, 0, 0, 0, "Silvermoon Champion");
INSERT INTO creature_text VALUES (33467, 1, 0, "All of this posturing, all of this dancing about in rings... it seems pointless. What's the use?", 12, 0, 100, 1, 0, 0, 0, "Silvermoon Champion");
INSERT INTO creature_text VALUES (33467, 2, 0, "And I'm beginning to get HELMET HAIR. This is a travesty!", 12, 0, 100, 1, 0, 0, 0, "Silvermoon Champion");
INSERT INTO creature_text VALUES (34102, 30, 0, "It may seem pointless, but try to find pride in a good fight. The time will come when all this practicing and competition will be put to good use.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 31, 0, "And your hair looks wonderful.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 32, 0, "Argent Confessor Paletress blows a kiss to Silvermoon Champion.", 16, 0, 100, 17, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (33467, 3, 0, "Silvermoon Champion blows a kiss to Argent Confessor Paletress.", 16, 0, 100, 17, 0, 0, 0, "Silvermoon Champion");
