INSERT INTO creature_text VALUES (33477, 0, 0, "My heart hangs heavier with each passing day, Confessor.", 12, 0, 100, 1, 0, 0, 0, "Stormwind Champion");
INSERT INTO creature_text VALUES (33477, 1, 0, "I have been ordered here, and here I stay and fight under the banner of Stormwind. But everything I do here is empty.", 12, 0, 100, 1, 0, 0, 0, "Stormwind Champion");
INSERT INTO creature_text VALUES (33477, 2, 0, "Beyond these reaches, the Scourge continue their work, destroying more and more of what we've built. The fight should be there. I should be there.", 12, 0, 100, 1, 0, 0, 0, "Stormwind Champion");
INSERT INTO creature_text VALUES (33477, 3, 0, "How can I justify jousting while people die without my protection?", 12, 0, 100, 1, 0, 0, 0, "Stormwind Champion");
INSERT INTO creature_text VALUES (34102, 40, 0, "Your heart is in the right place, and I know you seek to serve the Light. But these small strikes do little to the great undead host.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 41, 0, "This tournament is helping to form a strong, concerted force, though it may not appear obvious. When we're ready, the eyes of the Crusade and all its champions will turn towards Icecrown.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 42, 0, "We will bring the might of all races to the Lich King, and he will be defeated.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
INSERT INTO creature_text VALUES (34102, 43, 0, "I know this does not seem like the obvious path to you, but it will bring you closer to the Light. Fight well, brother.", 12, 0, 100, 1, 0, 0, 0, "Argent Confessor Paletress");
