INSERT INTO `gossip_menu_option` VALUES ('3701', '0', '0', 'You may speak frankly, Neeru...', '7266', '1', '1', '21272', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21272', '0', '0', 'It is good to see the Burning Blade is taking over where the Shadow Council once failed.', '7267', '1', '1', '21273', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21273', '0', '0', 'So the Searing Blade is expendable?', '7268', '1', '1', '21274', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21274', '0', '0', 'If there is anything you would have of me...', '7269', '1', '1', '21275', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21276', '0', '0', 'How did you know? I mean, yes... Yes I am looking for that shard. Do you have it?', '11034', '1', '1', '21277', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21277', '0', '0', 'Alright. Where?', '11036', '1', '1', '21278', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21278', '0', '0', 'By Bronzebeard\'s... um, beard! What are you talking about?', '0', '1', '1', '21279', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21279', '0', '0', 'Fish? You gave a piece of what could be the key to saving all life on Kalimdor to a fish?', '11040', '1', '1', '21280', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21280', '0', '0', 'A minnow? The oceans are filled with minnows! There could be a hundred million million minnows out there!', '11042', '1', '1', '21281', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21281', '0', '0', '...', '69357', '1', '1', '21282', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21282', '0', '0', 'You put the piece on a minnow and placed the minnow somewhere in the waters of the sea between here and the Eastern Kingdoms? And this minnow has special powers?', '11046', '1', '1', '21283', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21283', '0', '0', 'You\'re insane.', '11048', '1', '1', '21284', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21284', '0', '0', 'I\'m all ears.', '11050', '1', '1', '21285', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21285', '0', '0', 'Come again.', '11052', '1', '1', '21286', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21286', '0', '0', 'Ok, let me get this straight. You put the scepter entrusted to your Flight by Anachronos  on a minnow of your own making and now you expect me to build an... an arcanite buoy or something... to force your minnow out of hiding? AND potentially incur the wrath of an Elemental Lord? Did I miss anything? Perhaps I am to do this without any clothes on, during a solar eclipse, on a leap year?', '11054', '1', '1', '21287', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21287', '0', '0', 'FINE! And how, dare I ask, am I supposed to acquire an arcanite buoy?', '11057', '1', '1', '21288', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21288', '0', '0', 'But...', '77157', '1', '1', '0', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('21289', '0', '0', 'I believe in you.', '36640', '1', '1', '0', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('8336', '0', '0', 'I bring word from Lord Illidan.', '19260', '1', '1', '8342', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('8342', '0', '0', 'The cipher fragment is to be moved. Have it delivered to Zuluhed.', '19262', '1', '1', '8341', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('8341', '0', '0', 'Perhaps you did not hear me, Ruusk. I am giving you an order from Illidan himself!', '19266', '1', '1', '8340', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('8340', '0', '0', 'Very well. I will return to the Black Temple and notify Lord Illidan of your unwillingness to carry out his wishes. I suggest you make arrangements with your subordinates and let them know that you will be leaving this world soon.', '19268', '1', '1', '8339', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('8339', '0', '0', 'Do I need to go into all the gory details? I think we are both well aware of what Lord Illidan does with those that would oppose his word. Now, I must be going! Farewell, Ruusk! Forever...', '19270', '1', '1', '8338', '0', '0', '0', null, '0');
INSERT INTO `gossip_menu_option` VALUES ('8338', '0', '0', 'Ah, good of you to come around, Ruusk. Thank you and farewell.', '19272', '1', '1', '0', '0', '0', '0', null, '0');
